import  { useEffect, useState } from "react"
import { Row ,Col } from 'react-bootstrap';
import axios from 'axios';

export default function People() {
  // todo : fetch people data from "https://reqres.in/api/users" url
  // todo : list all the fetched people data in cards
  // todo : once any person clicked navigate that person details page
            

  const [people, setPeople] = useState([])

  const getPeople = async () => {
   
    const res = await axios.get('https://reqres.in/api/users');

    setPeople(res.data.data); /*  data from axios than pepole inside data */
  
  };

  useEffect(() => {
    getPeople();
  }, []);

  const navigate = useNavigate();
  return (
    <div className="text-center">
      <h1>People LIST</h1>
      <Row>{/*  placeholder for people data */}
            {people.map(item => (
               <Col
               key={item.id}
               sm={12}
               md={6}
               lg={4}
               type="button"
               className="mb-2"
               onClick={() => {navigate(`/people/`)}}
             >
                    <img src={item.avatar} alt="person" />
                     <h6>
                          {item.first_name} {item.last_name}
                      </h6>
                </Col>
      ))}

      </Row>
    </div>
  );
}
