import React from 'react';
import { Button, Card, Col, Form, Row } from 'react-bootstrap';

export default function Login() {
  // todo : make inputs controlled components
  // todo : handle form submit
  // todo : store user information in localstorage
  // todo : navigate user to home page after submit


  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  return (
    <Row className="justify-content-center">
      <Col sm={12} md={10} lg={6}>
        <Card className="mt-4">
          <Card.Body>
            <Form>
              <h1 className="text-center">Login</h1>
              <Form.Group className="mb-3">
                <Form.Label>Email</Form.Label>
                <Form.Control type="email" placeholder="john@email.com" value={email}
                  onChange={(e) => setEmail(e.target.name)}
                 /> {/* we need value onchange */}
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Password</Form.Label>
                
              </Form.Group>

              <div className="text-center">
                <Button type="submit" variant="primary">
                  Login
                </Button>
              </div>
            </Form>
          </Card.Body>
        </Card>
      </Col>
    </Row>
  );
}
